              Object.keys(categoriesMap).map((title)=> { return (
                <Fragment key={title}>
                  <h2>{title}</h2>
                  <div className='products-container'>
                    {categoriesMap[title].map((product) => (
                      <ItemCard key={product.id} product={product}/>
                    ))}
                  </div>
                  </Fragment>)
              })
            }



        </Fragment>
      )
    }
